SELECT MEMBER_ID
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

SELECT MEMBER_PROFILE
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

SELECT MEMBER_ID, MEMBER_NICKNAME, MEMBER_PROFILE, MEMBER_COVER_IMG
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

SELECT MEMBER_ID, MEMBER_NAME 
FROM MEMBER 
WHERE MEMBER_NAME = #{memberName} AND MEMBER_EAMIL = #{memberEmail}
