mapper.insert:
		INSERT INTO MEMBER_ROOM_NO (ROOM_NO, MEMBER_NO)
		SELECT #{roomNo}, #{memberNo} FROM DUAL
		WHERE NOT EXISTS (
    		SELECT 1 FROM MEMBER_ROOM_NO WHERE ROOM_NO = #{roomNo} AND MEMBER_NO = #{memberNo}
			)

mapper.delete:
		DELETE FROM MEMBER_ROOM_NO
		WHERE ROOM_NO = #{roomNo}
    	AND MEMBER_NO IN

	      #{member}
